mapper.select:
SELECT 

FROM catch_hinge_statistics a

WHERE a.id = #{id}

SELECT 

FROM catch_hinge_statistics a




		AND a.nyear = #{nyear}


		AND a.hinge_type = #{hingeType}




		ORDER BY ${page.orderBy}


		order by a.nyear

mapper.update:
UPDATE catch_hinge_statistics SET 	
	nyear = #{nyear},
	hinge_type = #{hingeType},
	hinge_number = #{hingeNumber}
WHERE id = #{id}
